<?phprequire 'App/Views/Website/HomeView.php';require 'App/Views/Website/ContatoView.php';require 'App/Views/Website/ErroView.php';require 'App/Views/Website/BlogView.php';require 'App/Views/Website/SingleView.php';require 'App/Views/Website/EscritorioView.php';require 'App/Views/Website/ServicosView.php';require 'App/Views/Website/ConsultaView.php';class WebsiteController {    private $home;    private $escritorio;     private $servicos;    private $blog;    private $contato;    private $single;    private $consulta;    public $erro;    public function __construct() {        $this->erro = new ErroView;        $this->home = new HomeView();         $this->servicos = new servicosView();        $this->escritorio = new EscritorioView();        $this->contato = new ContatoView();        $this->blog = new blogView();        $this->single = new SingleView();        $this->consulta = new ConsultaView();                            }    function showContentHomeAction() {        $this->home->showContent();    }    function showContentEscritorioAction() {        $this->escritorio->showContentEscritorio();    }    function showContentContatoAction() {        $this->contato->showContentContato();    }    function rotaInexistenteAction() {        $this->erro->showContentErroAction();    }    function blogAction() {                       $url = ltrim($_SERVER["REQUEST_URI"], '/');        $url = explode('/', $url);        $url = $url[0];        $url = explode('?', $url);                if ($url[0] === 'single') {             $this->single->showContentSingle();        } else {             $this->blog->showContentBlog();        }           }    function selecionaBannerAction() {        $this->home->selecionaBanner();    }        function servicosAction() {        $this->servicos->showContentServicos();    }        function AgendarConsultaAction() {        $this->consulta->showContentConsulta();    }    function agendaAction() {               $agendaModel = new ConsultasModel();        $agendaModel->insertConsultas();    }    function carregaCidadeAction() {        $agendaModel = new ConsultasModel();        $agendaModel->selectCidades();            }        function selectDatasAction() {        $this->consulta->consultaDatas();            }}