<?phpclass ContatoModel {   public function sendEmail() {        $mail = new PHPMailer;        $mail->IsSMTP();                                      // Set mailer to use SMTP        $mail->Host = 'smtp.gmail.com';                 // Specify main and backup server        $mail->Port = 587;                                    // Set the SMTP port        $mail->SMTPAuth = true;                               // Enable SMTP authentication        $mail->Username = 'alpronet@gmail.com';        // SMTP username        $mail->Password = 'jos101010';                      // SMTP password        $mail->SMTPSecure = 'tls';                            // Enable encryption, 'ssl' also accepted        $mail->From = 'alpronet@gmail.com';        $mail->FromName = 'Contato de ' . $_POST["nome"] . ' Pelo Site';        $mail->AddAddress('adm@alpronet.com.br');            // Add a recipient        $mail->IsHTML(true);                                  // Set email format to HTML        $mail->Subject = "Contato";        $mail->Subject = "Mensagem - Cliente " . $nome;        $mail->Body = "Nome: "                . utf8_decode($_POST["nome"])                . "<br>Email: "                . utf8_encode($_POST["email"])                . "<br>Fone: "                . utf8_encode($_POST["telefone"])                . "<br>Mensagem: "                . utf8_encode($_POST["mensagem"]);        if (!$mail->Send()) {            echo utf8_decode('Não foi possível enviar a mensagem!') . $mail->ErrorInfo;            exit;        } else {            echo file_get_contents("Web/Templates/Bar/index.html");            sleep(3);            die("<script type='text/javascript'>window.location.href='home';</script>");        }    }}