<?phpclass EmailModel {    private $Host = 'smtp.alpronet.com.br';    private $Password = 'KdeKdeKde01';    function __construct() {        $this->persistencia = new Persistence();    }    public function sendEmail() {        $dadosEmail['nome'] = $_POST['nome'];        $dadosEmail['email'] = $_POST['email'];        $dadosEmail['telefone'] = $_POST['telefone'];        $dadosEmail['mensagem'] = $_POST['mensagem'];        $this->persistencia->insertNew('Email', $dadosEmail);        $mail = new PHPMailer;        $mail->IsSMTP();                                      // Set mailer to use SMTP        $mail->Host = 'smtp.gmail.com';                 // Specify main and backup server        $mail->Port = 587;                                    // Set the SMTP port        $mail->SMTPAuth = true;                               // Enable SMTP authentication        $mail->Username = 'alpronet@gmail.com';        // SMTP username        $mail->Password = 'jos101010';                      // SMTP password        $mail->SMTPSecure = 'tls';                            // Enable encryption, 'ssl' also accepted        $mail->From = 'contato@centroregionaldeoptometria.com.br';        $mail->FromName = $_POST["nome"];        $mail->AddAddress('contato@centroregionaldeoptometria.com.br');            // Add a recipient        $mail->IsHTML(true);                                  // Set email format to HTML        $mail->Subject = " Contato Da Optometria";        $mail->Subject = $_POST["assunto"];        $mail->Body = "Nome: "                . utf8_decode($_POST["nome"])                . "<br>Email: "                . utf8_encode($_POST["email"])                . "<br>Fone: "                . utf8_encode($_POST["telefone"])                . "<br>Mensagem: "                . utf8_encode($_POST["mensagem"]);        if (!$mail->Send()) {            echo utf8_decode('Não foi possível enviar a mensagem!') . $mail->ErrorInfo;            exit;        } else {            echo file_get_contents("Web/Templates/Bar/index.html");            sleep(3);            die("<script type='text/javascript'>window.location.href='home';</script>");        }    }    public function insertEmail() {        $dados['nome_email'] = $_POST['nome_email'];        $dados['end_email'] = $_POST['email'];        $dados['fone_email'] = $_POST['fone_email'];        $dados['msg_email'] = $_POST['msg_email'];        $this->persistencia->insertNew('Email', $dados);          echo file_get_contents("Web/Templates/Bar/index.html");        sleep(3);        echo '<script>$("#container").hide();</script>';        echo file_get_contents("Web/Templates/noticias.html");        sleep(5);        die("<script type='text/javascript'>window.location.href='home';</script>");    }}