<?phpclass HomeModel {    function __construct() {        $this->persistencia = new Persistence();    }           public function selectBanner() {              $condition = 'status_slogan = "ATIVO"';        $extra = 'ORDER BY id_slogan DESC LIMIT 1 ';        $table = 'Slogan_Banner';        $valores = $this->persistencia->select($table, $condition, $extra);                return utf8_encode($valores[0]['banner_slogan']) ;    }        public function selectTitulo() {        $condition = 'status_slogan = "ATIVO"';        $extra = 'ORDER BY id_slogan DESC LIMIT 1 ';        $table = 'Slogan_Banner';        $valores = $this->persistencia->select($table, $condition, $extra);        return utf8_encode($valores[0]['titulo_slogan']) ;    }         public function selectSubTitulo() {        $condition = 'status_slogan = "ATIVO"';        $extra = 'ORDER BY id_slogan DESC LIMIT 1 ';        $table = 'Slogan_Banner';        $valores = $this->persistencia->select($table, $condition, $extra);        return utf8_encode($valores[0]['sub_titulo_slogan']) ;    }        public function selectSubTexto() {        $condition = 'status_slogan = "ATIVO"';        $extra = 'ORDER BY id_slogan DESC LIMIT 1 ';        $table = 'Slogan_Banner';        $valores = $this->persistencia->select($table, $condition, $extra);        return utf8_encode($valores[0]['end_banner_slogan']) ;    }        public function selectAgenda() {        $condition = 'status_agenda = "Ativa"';        $extra = 'ORDER BY id_agenda DESC LIMIT 3 ';        $table = 'Agenda';        $agenda = $this->persistencia->select($table, $condition, $extra);        $cidades = $this->persistencia->select('Cidades', 'status_cidade = "ATIVA"' );                   foreach ($cidades as $key => $value) {            $cidade[$value['id_cidade']] = $value['nome_cidade'];        }             for ($i = 0; $i < count($agenda) ; $i++) {            $dataTime = explode(' ', $agenda[$i]['data_agenda']) ;            $dataTime = $dataTime[0];            $city = $cidade[$agenda[$i]['cidade_agenda']];            $data = $this->inverteData($dataTime);            $meses = explode("/",$data);            $mes = $this->convertMes($meses[1]);            $dia = $meses[0];                        $texto .= '<div class="col-md-4 events-grid">                <div class="mnth-date">                    <div class="col-xs-5 mnth-date-left">                        <h4>'.$dia.' de<span>'.$mes.'</span></h4>                    </div>                    <div class="col-xs-7 mnth-date-right">                        <p>'.utf8_encode($agenda[$i]['titulo_agenda']).'</p>                        <p>'.utf8_encode($city).'</p>                        </div>                    <div class="clearfix"> </div>                                      <div class="more m1">                        <a href="agendar" class="hvr-curl-bottom-right">Agendar</a>                    </div>                </div>            </div>';                    }                return $texto ;            }    public function selectSlides() {                $condition = 'status_optometria = "Ativo"';                $table = 'Optometria';        $optometria = $this->persistencia->select($table, $condition);                    $classe[0] ="#55C3B6";        $classe[1] ="#B46425";        $classe[2] ="#999";                for ($i = 0; $i < count($optometria); $i++) {                        $texto .= '<li>                    <div style="background:'.$classe[$i].';" class="banner-pos-grid1">                        <div class="col-xs-4 banner-pos-grid1-left">                            <span class="'.$optometria[$i]['icone_optometria'].'" aria-hidden="true"></span>                        </div>                        <div class="col-xs-8 banner-pos-grid1-right">                            <h3>'.utf8_encode($optometria[$i]['titulo_optometria']).'</h3>                        </div>                        <div class="clearfix"> </div>                        <p>'.utf8_encode($optometria[$i]['texto_optometria']).'</p>                    </div>                </li>';                    }                return $texto ;    }            public function selectServicos() {        $condition = 'status_servico = "Ativo"';        $table = 'Sevicos';        $sevicos = $this->persistencia->select($table, $condition);                                   for ($i = 0; $i < count($sevicos); $i++) {                        $texto .='<article style="position: absolute; width: 100%; opacity: 0;">                     <div class="banner-wrap">                        <div class="banner-bottom-info">                            <h3>'.utf8_encode($sevicos[$i]['titulo_servico']).'</h3>                            <p>'. utf8_encode($sevicos[$i]['texto_servico']).'</p>                        </div>                    </div>                </article>';                    }                                return $texto;                    }          public function selectParceiros() {        $condition = 'status_parceiro = "ATIVO"';        $table = 'Parceiros';        $parceiros = $this->persistencia->select($table, $condition);        //        print_r($parceiros);die();                                   for ($i = 0; $i < count($parceiros); $i++) {            if ($i === 0) {                $texto .='<div class="item active">                            <a href="'. $parceiros[$i]['link_parceiro'].'" target="_blank">                                <img src="sistema/images/'. $parceiros[$i]['imagem_parceiro'].'" alt="">                            </a>                                  </div>';            } else {                $texto .='<div class="item ">                                <img src="sistema/images/'. $parceiros[$i]['imagem_parceiro'].'" alt="">                          </div>';            }                                }                               return $texto;                    }         public function selectPacientes() {        $condition = 'status_Paciente = "ATIVO"';        $table = 'Testemunhos';        $extra = 'ORDER BY id_Paciente DESC LIMIT 3 ';        $pacientes = $this->persistencia->select($table, $condition,$extra);                for ($i = 0; $i < count($pacientes); $i++) {                        $texto .=' <div class="col-md-4 testimonials-grid">                <div class="col-xs-4 testimonials-grid-left">                    <img src="sistema/images/'.$pacientes[$i]['foto_paciente'].'" alt=" " class="img-responsive" />                </div>                <div class="col-xs-8 testimonials-grid-right">                    <img src="Web/Imagens/1.png" alt=" " class="img-responsive" />                    <div class="testimonials-grid-right-p">                        <p>'.utf8_encode($pacientes[$i]['texto_paciente']).'<span>'. utf8_encode($pacientes[$i]['nome_paciente']).'</span></p>                    </div>                </div>                <div class="clearfix"> </div>            </div>';                    }                                return $texto;                    }        public function inverteData($data) {        if (count(explode("/", $data)) > 1) {            $dataInvert = implode("-", array_reverse(explode("/", $data)));            $dataExplode = explode("-", $dataInvert);            $dataFinal = $dataExplode[0] . '-' . $dataExplode[1] . '-' . $dataExplode[2];            return $dataFinal;        } elseif (count(explode("-", $data)) > 1) {            $dataInvert = implode("/", array_reverse(explode("-", $data)));            $dataExplode = explode("/", $dataInvert);            $dataFinal = $dataExplode[0] . '/' . $dataExplode[1] . '/' . $dataExplode[2];            return $dataFinal;        }    }        public function convertMes($param) {        switch ($param) {            case "01": $mes = Janeiro;                break;            case "02": $mes = Fevereiro;                break;            case "03": $mes = Mar√ßo;                break;            case "04": $mes = Abril;                break;            case "05": $mes = Maio;                break;            case "06": $mes = Junho;                break;            case "07": $mes = Julho;                break;            case "08": $mes = Agosto;                break;            case "09": $mes = Setembro;                break;            case "10": $mes = Outubro;                break;            case "11": $mes = Novembro;                break;            case "12": $mes = Dezembro;                break;        } return $mes;    }}