<?phpclass ServicosModel {    function __construct() {        $this->persistencia = new Persistence();    }    public function selectServicos() {        $condition = 'status_servico = "Ativo"';        $table = 'Sevicos';        $sevicos = $this->persistencia->select($table, $condition);        for ($i = 0; $i < count($sevicos); $i++) {            $texto .= '<div class="col-md-3 featured-services-grid">					<div class="featured-services-grd">						<span class="' . $sevicos[$i]['icone_servico'] . '" aria-hidden="true"></span>						<h4>' . utf8_encode($sevicos[$i]['titulo_servico']) . '</h4>						<p>' . utf8_encode($sevicos[$i]['texto_servico']) . '</p>											</div>				</div>';        }        return $texto;    }    public function selectServicosAdicionais() {        $condition = 'status_servico = "Ativo" AND adicional_servico= "Sim"';        $table = 'Sevicos';        $sevicos = $this->persistencia->select($table, $condition);        for ($i = 0; $i < count($sevicos); $i++) {            $texto .= '<div class="col-md-6 additional-service-grd">						<img src="sistema/images/' . $sevicos[$i]['foto_servico'] . '" alt="ServiÃ§os Adicionais" class="img-responsive" />						<p>' . utf8_encode($sevicos[$i]['texto_servico']) . '</p>											</div>';        }        return $texto;    }    public function selectServicosLista() {        $condition = 'status_servico = "Ativo"';        $table = 'Sevicos';        $sevicos = $this->persistencia->select($table, $condition);        $numero = count($sevicos);        $numPorColuna = $numero / 3;        for ($in = 0; $in < 2; $in++) {            for ($i = 0; $i < $numPorColuna; $i++) {                $texto .= '<div class="col-md-4 sevices-grid">					<ul>						<li><a>' . utf8_encode($sevicos[$i]['titulo_servico']) . '</a></li>											</ul>				</div>';            }        }        return $texto;    }}