<?phpclass BlogView {    private $noticias;    private $base = 'Web/Contents/Blog';    private $includes = 'Web/Includes';    protected $template;    function __construct() {        $this->template = new Template();        $this->noticias = new BlogModel();    }    function showContentBlog() {        $this->buildHTML();    }    public function assignData() {        $this->template->assign('@miniatura', $this->noticias->selectMiniaturas());        $this->template->assign('@noticia', $this->noticias->selectNoticias());        $this->template->assign('@categorias', $this->noticias->selectCategorias());        $this->template->assign('@menuBlog', 'active');    }    public function assignIncludes() {        $this->template->assign('@content', $this->base . '/content.php');        $this->template->assign('@menu', $this->includes . '/menu.html');        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@footer', $this->includes . '/footer.html');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function buildHTML() {        $this->assignTemplate();        $this->assignIncludes();        $this->assignData();        $this->template->renderHTML();    }}