<?phprequire 'App/Models/ConsultasModel.php';class ConsultaView {    private $consulta;    private $base = 'Web/Contents/Consultas';    private $includes = 'Web/Includes';    protected $template;    function __construct() {        $this->template = new Template();        $this->consulta = new ConsultasModel();    }        public function consultaDatas() {                return $this->consulta->selectOptions();    }    function showContentConsulta() {         $this->buildHTML();    }    public function assignData() {        $this->template->assign('@agendar', 'active');         $this->template->assign('@cidades', $this->consulta->selectCidades());    }    public function assignIncludes() {        $this->template->assign('@content', $this->base . '/content.php');        $this->template->assign('@menu', $this->includes . '/menu.html');        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@footer', $this->includes . '/footer.html');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function buildHTML() {        $this->assignTemplate();        $this->assignIncludes();        $this->assignData();        $this->template->renderHTML();    }}