<?phprequire 'App/Models/ContatoModel.php';class ContatoView {    private $contato;    private $base = 'Web/Contents/Contato';    private $includes = 'Web/Includes';    protected $template;    function __construct() {        $this->contato = new ContatoModel();        $this->template = new Template();    }    function showContentContato() {        $assignTemplate = $this->buildHTML();    }    public function assignData() {        $this->template->assign('@content', $this->base . '/content.php');    }    public function assignIncludes() {        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@menu', $this->includes . '/menu.html');        $this->template->assign('@menuContato', 'active');        $this->template->assign('@conActive', 'active');        $this->template->assign('@topo', $this->includes . '/topo.html');        $this->template->assign('@page', 'Contato');        $this->template->assign('@footer', $this->includes . '/footer.html');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function buildHTML() {        $this->assignTemplate();        $this->assignData();        $this->assignIncludes();        $this->template->renderHTML();    }}