<?phprequire 'App/Models/EmpresaModel.php';class EmpresaView {    private $home;    private $empresa;    private $base = 'Web/Contents/Empresa';    private $includes = 'Web/Includes';    protected $template;    function __construct() {        $this->home = new HomeModel();        $this->template = new Template();        $this->empresa = new EmpresaModel();    }    function showContentEmpresa() {        $this->buildHTML();    }    public function assignData() {        $this->template->assign('@content', $this->base . '/content.php');           }    public function assignIncludes() {         $this->template->assign('@menu', $this->includes . '/menu.html');        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@topo', $this->includes . '/topo.html');        $this->template->assign('@page','Empresa');        $this->template->assign('@footer', $this->includes . '/footer.html');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function buildHTML() {        $this->assignTemplate();        $this->assignData();        $this->assignIncludes();        $this->template->renderHTML();    }}