<?phpinclude 'App/Models/EscritorioModel.php';class EscritorioView {    private $base = 'Web/Contents/Escritorio';    private $includes = 'Web/Includes';    private $escritorio;    protected $template;    function __construct() {        $this->escritorio = new EscritorioModel();        $this->template = new Template();    }    function showContentEscritorio() {        $assignTemplate = $this->buildHTML();    }    public function assignData() {        $this->template->assign('@content', $this->base . '/content.php');        $this->template->assign('@menu', $this->includes . '/menu.html');    }    public function assignIncludes() {        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@topo', $this->includes . '/topo.html');        $this->template->assign('@titulo', $this->escritorio->exibeTitulo());        $this->template->assign('@subTitulo', $this->escritorio->exibeSubTitulo());        $this->template->assign('@equipe', $this->escritorio->exibeEquipe());        $this->template->assign('@texto', $this->escritorio->exibeTexto());        $this->template->assign('@imagem', $this->escritorio->exibeImagem());        $this->template->assign('@valores', $this->escritorio->exibeValores());        $this->template->assign('@ultValores', $this->escritorio->exibeValores2());        $this->template->assign('@escAtive', 'active');        $this->template->assign('@footer', $this->includes . '/footer.html');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function buildHTML() {        $this->assignTemplate();        $this->assignData();        $this->assignIncludes();        $this->template->renderHTML();    }}