<?phprequire 'App/Models/HomeModel.php';include 'App/Models/BlogModel.php';class HomeView {    private $home;    private $blog;    private $base = 'Web/Contents/Home';    private $includes = 'Web/Includes';    protected $template;    function __construct() {        $this->home = new HomeModel();        $this->blog = new BlogModel();        $this->template = new Template();    }    function showContent() {        $this->buildHTML();    }    public function assignData() {        $this->template->assign('@content', $this->base . '/content.php');    }    public function assignIncludes() {        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@teste', 'teste');        $this->template->assign('@topo', $this->includes . '/topo.html');         $this->template->assign('@footer', $this->includes . '/footer.html');                 $this->template->assign('@banner', $this->home->selectBanner());               $this->template->assign('@titulo', $this->home->selectTitulo());        $this->template->assign('@SubTitulo', $this->home->selectSubTitulo());        $this->template->assign('@SubTexto', $this->home->selectSubTexto());        $this->template->assign('@optometia', $this->home->selectSlides());             $this->template->assign('@agenda', $this->home->selectAgenda());        $this->template->assign('@servicos', $this->home->selectServicos());        $this->template->assign('@parceiros', $this->home->selectParceiros());        $this->template->assign('@pacientes', $this->home->selectPacientes());                $this->template->assign('@menu', $this->includes . '/menu.html');        $this->template->assign('@inicial', 'active');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function selecionaBanner() {        $this->home->selectBanner();    }    public function buildHTML() {        $this->assignTemplate();        $this->assignData();        $this->assignIncludes();        $this->template->renderHTML();    }}