<?phpclass MostraProdutosSiteView {    private $produtos;    private $base = 'Web/Contents/Produtos';    private $includes = 'Web/Includes';     protected $template;    function __construct() {        $this->produtos = new ProdutosModel();        $this->template = new Template();    }        function showContentProdutos() {        $this->buildHTML();    }    public function assignData() {        $this->template->assign('@content', $this->base . '/content.php');        $this->template->assign('@produtos', $this->produtos->produtosPrdutos());           }    public function assignIncludes() {        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@footer', $this->includes . '/footer.html');        $this->template->assign('@menu', $this->includes . '/menu.html');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function buildHTML() {        $this->assignTemplate();        $this->assignData();        $this->assignIncludes();        $this->template->renderHTML();    }}