<?phprequire 'App/Models/ServicosModel.php';class ServicosView {    private $servicos;    private $base = 'Web/Contents/Servicos';    private $includes = 'Web/Includes';    protected $template;    function __construct() {             $this->template = new Template();        $this->servicos = new ServicosModel();    }    function showContentServicos() {         $this->buildHTML();    }    public function assignData() {        $this->template->assign('@content', $this->base . '/content.php');           }    public function assignIncludes() {        $this->template->assign('@header', $this->includes . '/header.html');        $this->template->assign('@topo', $this->includes . '/topo.html');        $this->template->assign('@footer', $this->includes . '/footer.html');        $this->template->assign('@menu', $this->includes . '/menu.html');        $this->template->assign('@cervicos', $this->servicos->selectServicos());        $this->template->assign('@adicionais', $this->servicos->selectServicosAdicionais());        $this->template->assign('@lista', $this->servicos->selectServicosLista());                $this->template->assign('@service', 'active');    }    public function assignTemplate() {        $this->template->setLayout('Web/Templates/template.html');    }    public function buildHTML() {        $this->assignTemplate();        $this->assignData();        $this->assignIncludes();        $this->template->renderHTML();    }}