<?phprequire 'App/Conn/Persistence.php';class Router {    private $percistence;    private $sistema;    private $website = array(        #WEBSITE        0 => array("rota" => "home", "controller" => "Website", "method" => "showContentHome"),        1 => array("rota" => "empresa", "controller" => "Website", "method" => "showContentEmpresa"),        2 => array("rota" => "contato", "controller" => "Website", "method" => "showContentContato"),        3 => array("rota" => "sistema", "controller" => "Principal", "method" => "showContent"),        4 => array("rota" => "login", "controller" => "Principal", "method" => "login"),        5 => array("rota" => "escritorio", "controller" => "Website", "method" => "showContentEscritorio"),        6 => array("rota" => "eniviarEmail", "controller" => "Email", "method" => "eniviarEmail"),        7 => array("rota" => "servicos", "controller" => "Website", "method" => "servicos"),        8 => array("rota" => "blog", "controller" => "Website", "method" => "blog"),        9 => array("rota" => "relatorio", "controller" => "Relatorio", "method" => "relatorio"),        10 => array("rota" => "montaResumo", "controller" => "Relatorio", "method" => "montaResumo"),        11 => array("rota" => "selecionaBanner", "controller" => "Website", "method" => "selecionaBanner"),        12 => array("rota" => "noticias", "controller" => "Website", "method" => "blog"),        13 => array("rota" => "single", "controller" => "Website", "method" => "blog"),        14 => array("rota" => "enviarEmailSite", "controller" => "Email", "method" => "eniviarEmail"),        15 => array("rota" => "agendar", "controller" => "Website", "method" => "AgendarConsulta"),        16 => array("rota" => "agenda", "controller" => "Website", "method" => "agenda"),        17 => array("rota" => "assine", "controller" => "Email", "method" => "assine"),        18 => array("rota" => "carregaCidade", "controller" => "Website", "method" => "carregaCidade"),        18 => array("rota" => "selectDatas", "controller" => "Website", "method" => "selectDatas"),    );    function __construct() {        $this->percistence = new Persistence();    }    public function selecionaSite($url) {        for ($i = 0; $i < count($this->website); $i++) {            if ($this->website[$i]['rota'] == $url) {                $site['rota'] = $this->website[$i]['rota'];                $site['controller'] = $this->website[$i]['controller'];                $site['method'] = $this->website[$i]['method'];            }        }        return $site;    }    public function selecionaSistema() {        if (isset($_SESSION['usuario'])) {            $website = $this->percistence->select('permissoes', 'usuarios_id_usuario =' . $_SESSION['usuario'] . ' AND status = 1');          $temp = $this->percistence->select('usuarios', 'id_usuario =' . $_SESSION['usuario'] . ' AND status_usuario = 1');            $_SESSION["nome_usuario"] = $temp[0]["nome_usuario"];            $_SESSION["login_usuario"] = $temp[0]["login_usuario"];            $_SESSION["senha_usuario"] = $temp[0]["senha_usuario"];            $_SESSION["id_usuario"] = $temp[0]["id_usuario"];            $_SESSION["nivel_usuario"] = $temp[0]["nivel_usuario"];            $_SESSION["foto_usuario"] = 'src="' . $temp[0]["foto_usuario"] . '" ';            $_SESSION["data_usuario"] = date('M/Y', strtotime(date($temp[0]["data_usuario"])));            return $website;        }    }}