<?phpclass Template {    private $layout;    private $values = array();    private $content;    private $menu;    public function isReal($file) {        if (!file_exists("$file")) {            die("$file nÃ£o encontrado");        }    }    public function assign($label, $value, $ignore = false) {        if (!$ignore) {            if (@is_file($value)) {                $value = file_get_contents($value);            }            $this->setValues($label, $value);        } else {            $this->setValues($label, $value);        }    }    public function renderHTML() {        $layout = file_get_contents($this->getLayout());        foreach ($this->getValues() as $key => $value) {            $layout = str_replace($key, $value, $layout);        }        echo $layout;    }    public function getLayout() {        return $this->layout;    }    public function getValues() {        return $this->values;    }    public function setLayout($layout) {        $this->layout = $layout;    }    public function setValues($label, $value) {        $this->values[$label] = $value;    }    public function getContent() {        return $this->content;    }    public function setContent($content) {        $this->content = $content;    }    public function getMenu() {        return $this->menu;    }    public function setMenu($menu) {        $this->menu = $menu;    }}